import styled from 'styled-components';

export const FigureLeft = styled.div`
  position: absolute;
  top: 22VH;
  height: 20VH;
  left: 0VW;
  width: 50VW;
  background-color: #ED145B;
  border-radius: 0 100px 100px 0;
  z-index: -9;
`;

export const FigureRight = styled.div`
  position: absolute;
  top: 7VH;
  height: 20VH;
  right: 0VW;
  width: 45VW;
  background-color: #ED145B;
  border-radius: 100px 0 0 100px;
  z-index: -9;
`;

export const Name = styled.div`
    position: absolute;
    top: 3VH;
    left: 3VW;
    color: #1F2A44;
    font-size: 10VW;
    width: 25VW;
    height: 30VH;
`

export const Title = styled.div`
    width: 100VW;
    text-align: center;
    position: absolute;
    color: #1F2A44;
    top: 30VH;
    font-size: 12VW;
`

export const Button = styled.div`
    top: 50VH;
    left: 15VW;
    position: absolute;
    width: 70VW;
    height: 15VH;
    background-color: #63B1BC;
    border-radius: 100px;
    text-align: center;
    font-size: 10VW;
    line-height: 14VH;
    color: white;
`
